import { useState } from "react";
import "./App.css";

export default function App() {
  const [bill, setBill] = useState(0);
  const [tip, setTip] = useState(0);

  function handleBill(input) {
    console.log(input);
    setBill(input);
  }

  function handleTip(tip) {
    console.log(tip);
    setTip(tip);
  }

  return (
    <div>
      <BillInput onBill={handleBill} />
      <SelectPercentage
        title="How did you like the service?"
        onTip={handleTip}
        tip={tip}
      />
      <SelectPercentage
        title="How did your friend like the service?"
        onTip={handleTip}
        tip={tip}
      />
      <TotalAmount />
      <Reset />
    </div>
  );
}

function BillInput({ onBill }) {
  return (
    <div>
      <span>
        How much was the bill?
        <input
          type="text"
          placeholder="$0"
          value=""
          onChange={(b) => {
            onBill(b.target.value);
          }}
        />
      </span>
    </div>
  );
}

function SelectPercentage({ title, onTip, tip }) {
  return (
    <div>
      <span>
        {title}
        <select value={tip} onChange={(e) => onTip(e.target.value)}>
          <option value="0">Dissatisfied (0%)</option>
          <option value="5">It was Okay (5%)</option>
          <option value="10">It was good (10%)</option>
          <option value="20">Absolutely amazing! (20%)</option>
        </select>
      </span>
    </div>
  );
}

function TotalAmount() {
  return <div></div>;
}

function Reset() {
  return <div></div>;
}
