import { useState } from "react";
import "./App.css";

export default function App() {
  const [bill, setBill] = useState(0);
  const [tip, setTip] = useState(0);
  const [friendTip, setFriendTip] = useState(0);

  function handleBill(input) {
    setBill(input);
  }

  function handleTip(tip) {
    setTip(tip);
    setFriendTip(tip);
  }

  function resetTip() {
    setTip(0);
  }
  return (
    <div>
      <BillInput bill={bill} onBill={handleBill} />
      <SelectPercentage
        title="How did you like the service?"
        onTip={handleTip}
      />
      <SelectPercentage
        title="How did your friend like the service?"
        onTip={handleTip}
      />
      <TotalAmount bill={bill} tip={tip} friendTip={friendTip} />
      <Reset />
    </div>
  );
}

function BillInput({ bill, onBill }) {
  return (
    <div>
      <span>
        How much was the bill?
        <input
          type="text"
          placeholder="$0"
          value={bill}
          onChange={(b) => {
            onBill(Number(b.target.value));
          }}
        />
      </span>
    </div>
  );
}

function SelectPercentage({ title, onTip }) {
  return (
    <div>
      <span>
        {title}
        <select onChange={(e) => onTip(Number(e.target.value))}>
          <option value="0">Dissatisfied (0%)</option>
          <option value="5">It was Okay (5%)</option>
          <option value="10">It was good (10%)</option>
          <option value="20">Absolutely amazing! (20%)</option>
        </select>
      </span>
    </div>
  );
}

function TotalAmount({ bill, tip, friendTip }) {
  const averageTip = tip / friendTip;
  const finalTip = Math.floor((averageTip / bill) * 100);
  console.log(finalTip);
  const totalAmount = finalTip + bill;
  return (
    <div>
      <h1>
        You pay ${totalAmount} (${bill} + ${averageTip} tip)
      </h1>
    </div>
  );
}

function Reset() {
  return (
    <div>
      <button>Reset</button>
    </div>
  );
}
