import "./App.css";

export default function App() {
  const title = "How did you like the service?";
  return (
    <div>
      <BillInput />
      <SelectPercentage title={title} />
      <TotalAmount />
      <Reset />
    </div>
  );
}

function BillInput() {
  return (
    <div>
      <span>
        How much was the bill?
        <input type="text" placeholder="$0" value="" onChange={() => {}} />
      </span>
    </div>
  );
}

function SelectPercentage({ title }) {
  return (
    <div>
      <span>
        title
        <select>
          <option value="dissatisfied">Dissatisfied (0%)</option>
          <option value="okay">It was Okay (5%)</option>
          <option value="good">It was good (10%)</option>
          <option value="amazing">Absolutely amazing! (20%)</option>
        </select>
      </span>
    </div>
  );
}

function TotalAmount() {
  return <div></div>;
}

function Reset() {
  return <div></div>;
}
